mapper.select:
SELECT fish_zone_id, fish_zone_name, fish_zone_thumbnail, fish_zone_intro, fish_zone_charge, fish_zone_night_enable, fish_zone_longitude, fish_zone_latitude, fish_zone_address_description, create_time FROM fish_zone WHERE 1 = 1 AND del_flag = 0
AND fish_zone_id = #{fishPondId}
AND fish_zone_name = #{fishPondName}

SELECT fm.fish_man_id, fm.fish_man_name, fm.fish_man_tel, fm.create_time FROM fish_man fm, fish_zone fz WHERE 1 = 1 AND fm.del_flag = 0 AND fz.del_flag = 0
AND fz.fish_zone_id = #{fishPondId}
AND fm.fish_man_id = fz.fish_man_id ORDER BY fm.create_time ASC

SELECT ft.fish_type_id, ft.fish_type_name, ft.create_time FROM fish_type ft, fish_type_mapping ftm WHERE 1 = 1 AND ft.del_flag = 0 AND ftm.del_flag = 0 AND ft.fish_type_id = ftm.fish_type_id
AND ftm.fish_zone_id = #{fishPondId}
ORDER BY ft.create_time ASC

SELECT fzt.fish_zone_type_id, fzt.fish_zone_type_name, fzt.create_time FROM fish_zone_type fzt, fish_zone_type_mapping fztm WHERE 1 = 1 AND fzt.del_flag = 0 AND fztm.del_flag = 0 AND fzt.fish_zone_type_id = fztm.fish_zone_type_id
AND fztm.fish_zone_id = #{fishPondId}
ORDER BY fzt.create_time ASC
